import json
import boto3

ssm = boto3.client('ssm')

#get parameter from SSM parameter store
def get_parameter_store(name, decryption=False):
    try:
        parameter = ssm.get_parameter(Name=name, WithDecryption=decryption)
        return parameter['Parameter']['Value']
    except Exception as e:
        print(e)
        raise Exception('Error access paramter '+name+' from SSM Parameter Store')